import React from "react";
import "./Country.css";
import { Link } from "react-router-dom";

function separarEnMiles(x) {
    x = x.toString();
    var pattern = /(-?\d+)(\d{3})/;
    while (pattern.test(x))
        x = x.replace(pattern, "$1.$2");
    return x;
}

export default function Country({flag, name, continent,population}) {
    return [
        <div className="Country">
            
            <img src={flag} alt="img not found"/>
            <div>
            <Link to={`/country/${name}`}>
            <a className="name">{name}</a>
            </Link>
            </div>
            <div>
            <span className="continent">{continent}</span>
            </div>
            <div>
            <span>{separarEnMiles(population)}</span>
            </div>
        </div>
    ]
}
