import React from "react";
import { getCountry } from "../actions";
import { useDispatch, useSelector } from "react-redux";
import { useEffect } from "react";
import "./Detail.css";

export default function Detail(props) {
    const dispatch = useDispatch();
    const country = useSelector(state => state.detail);
    useEffect(() => {
        dispatch(getCountry(props.match.params.name));
    },[]);

    return (
        <div className="Detail">
            <h1>{country[0].id}</h1>
            <img src={country[0].flag} alt="flag not found" />
            <h2>{country[0].name}</h2>
            <h3>Continente: {country[0].continent}</h3>
            <h3>Capital: {country[0].capital}</h3>
            <h4>Subregión: {country[0].subregion}</h4>
            <h4>Area: {country[0].area}km²</h4>
            <h4>Población: {country[0].population}</h4>
        </div>
    )

        
}
