import React from "react";
import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getCountries } from "../actions";
import { Link } from "react-router-dom";
import Country from "./Country";

export default function Home() {

    // const dispatch = useDispatch();
    // const allCountries = useSelector(state => state.countries);

    // useEffect(() => {
    //     dispatch(getCountries());
    // },[]
    // );

    return(
        <div>
            <div>
                <input type="text" placeholder="Ingresa país..." />
                <button type="submit">Buscar</button>
            </div>
            <select>
                <option value="asc">A-Z</option>
                <option value="desc">Z-A</option>
            </select>
            <select name="continente">
                <option value="afr">Africa</option>
                <option value="nor">America del Norte</option>
                <option value="sur">America del Sur</option>
                <option value="asi">Asia</option>
                <option value="eur">Europa</option>
                <option value="oce">Oceanía</option>
            </select>
            {/* <h1>Lista de paises</h1>
            {allCountries && allCountries.map( c => {
                <Country name={c.name} img={c.img} continent={c.continent}/>
            })} */}
        </div>
    )
}
