                <button type="submit">Buscar</button>
            </div>
            <div>
            <a>Ordenar: </a>
            <button>A-Z</button>
            <button>Población</button>
            <select>
                <option value="asc">↓</option>
                <option value="desc">↑</option>
            </select>
            </div>
            <div>
            <a>Filtrar por: </a>
            <select name="continente">
                <option value="non">Continente</option>
                <option value="afr">Africa</option>
                <option value="nor">America del Norte</option>
                <option value="sur">America del Sur</option>
                <option value="ant">Antarctica</option>
                <option value="asi">Asia</option>
                <option value="eur">Europa</option>
                <option value="oce">Oceanía</option>
            </select>
            <select name="actividad">
                <option value="act">--</option>
            </select>
            </div>
            <div>
                {allCountries?.sort((a,b)=> a.name>b.name?1:-1).slice(0,9).map( p =>
                    <Country
                    key = {p.id}
                    flag = {p.flag}
                    name = {p.name}
                    continent = {p.continent}
                    />
                    )}
            </div>
        </div>
    )
}
