import React from "react";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getCountries, filterByContinent, orderBy, getActivities } from "../actions";
import Country from "./Country";
import Paginado from "./Paginado";
import SearchBar from "./SearchBar";
import "./Home.css";

export default function Home() {
    const dispatch = useDispatch();
    const allCountries = useSelector(state => state.countries);
    const allActivities = useSelector(state => state.activities)
    useEffect(() => {
        dispatch(getCountries());
        dispatch(getActivities());
    },[]);
 
    const [render, setRender] = useState("");
    const [currentPage, setCurrentPage] = useState(1);
    const [countriesPerPage, setCountriesPerPage] = useState(10);
    const indexOfLastCountry = currentPage * countriesPerPage;
    const indexOfFirstCountry = indexOfLastCountry - countriesPerPage;
    const currentCountries = allCountries.slice(indexOfFirstCountry, indexOfLastCountry);

    const paginado = pageNum => {
        setCurrentPage(pageNum);
    }

    function handleFilterCont(e) {
        dispatch(filterByContinent(e.target.value));
        setCurrentPage(1);
    }

    function handleOrderBy(e) {
        e.preventDefault();
        if (e.target.value === "AZ") dispatch(getCountries());
        else dispatch(orderBy(e.target.value));
        setCurrentPage(1);
        setRender(`Ordenado de forma: ${e.target.value}`);
    }

    return(
        <div className="home">
            <div> 
               <SearchBar/>
            </div>
            <div>
            <a>Ordenar por: </a>
            <select name="orden" onChange={e => handleOrderBy(e)}>
                <option value="AZ">A-Z</option>
                <option value="ZA">Z-A</option>
                <option value="PMm">Población (mayor a menor)</option>
                <option value="PmM">Población (menor a mayor)</option>
            </select>
            </div>
            <div>
            <a>Filtrar: </a>
            <select name="continente" onChange={e => handleFilterCont(e)}>
                <option value="all">Todos</option>
                <option value="Africa">Africa</option>
                <option value="North America">America del Norte</option>
                <option value="South America">America del Sur</option>
                <option value="Antarctica">Antarctica</option>
                <option value="Asia">Asia</option>
                <option value="Europe">Europa</option>
                <option value="Oceania">Oceanía</option>
            </select>
            <select name="actividad">
                <option value="--">--</option>
                {allActivities.map( a => (
                    <option value={a.name}>{a.name}</option>
                ))}
            </select>
            </div>
            <div>
            <Paginado 
            countriesPerPage={countriesPerPage}
            allCountries={allCountries.length}
            paginado={paginado}
            />
            </div>
            <div>
                {currentCountries?.map( p =>
                    <Country
                    key = {p.id}
                    id={p.id}
                    flag = {p.flag}
                    name = {p.name}
                    continent = {p.continent}
                    population = {currentCountries[0].name==="China"||currentCountries[0].name==="Heard Island and McDonald Islands" || currentCountries[0].name ==="Bouvet Island"?`${p.population} habitantes`:undefined}
                    />
                    )}
            </div>
        </div>
    )
}
