import { GET_COUNTRIES, FILTER_BY_CONTINENT, ORDER_BY, GET_COUNTRY, GET_ACTIVITIES, POST_ACTIVITY, FILTER_BY_ACTIVITY } from "../actions";
const initialState = {
    countries: [],
    allCountries: [],
    activities: [],
    allActivities: []
}

function rootReducer (state=initialState, action) {
    switch(action.type) {
        case GET_COUNTRIES:
            return {
                ...state,
                countries: action.payload.sort((a,b)=> a.name>b.name?1:-1),
                allCountries: action.payload.sort((a,b)=> a.name>b.name?1:-1)
            }
        case GET_ACTIVITIES:
            return {
                ...state,
                activities: action.payload,
                allActivities: action.payload
                }
        case FILTER_BY_CONTINENT:
            const allCountries = state.allCountries;
            const continentFilter = action.payload === "all"? allCountries: allCountries.filter( a => a.continent === action.payload)
            return {
                ...state,
                countries: continentFilter
            }
        case FILTER_BY_ACTIVITY:
            const allCountries3 = state.allCountries.filter( a => a.activities.some( b => b.name === action.payload))
            return {
                ...state,
                countries: allCountries3
            }
        case ORDER_BY:
            const allCountries2 = state.allCountries;
            const orderPopulation = () => {
                if(action.payload === "PMm") return allCountries2.sort((a,b) => a.population>b.population?-1:1);
                if(action.payload === "PmM") return allCountries2.sort((a,b) => a.population>b.population?1:-1);
                if(action.payload === "ZA") return allCountries2.sort((a,b)=> a.name>b.name?-1:1);
            }      
            return {
                ...state,
                countries: orderPopulation()
            }
        case GET_COUNTRY:
            return {
                ...state,
                countries: action.payload
            }
        case POST_ACTIVITY:
            return {
                ...state,
            }
        default:
            return state;
        
    }
}

export default rootReducer;
