import { GET_COUNTRIES, FILTER_BY_CONTINENT, ORDER_BY } from "../actions";
const initialState = {
    countries: [],
    allCountries: [],
}

function rootReducer (state=initialState, action) {
    switch(action.type) {
        case GET_COUNTRIES:
            return{
                ...state,
                countries: action.payload.sort((a,b)=> a.name>b.name?1:-1),
                allCountries: action.payload.sort((a,b)=> a.name>b.name?1:-1)
            }
        case FILTER_BY_CONTINENT:
            const allCountries = state.allCountries;
            const continentFilter = action.payload === "all"? allCountries: allCountries.filter( a => a.continent === action.payload)
            return {
                ...state,
                countries: continentFilter
            }
        case ORDER_BY:
            const allCountries2 = state.allCountries;
            const orderPopulation = () => {
                if(action.payload === "PMm") return allCountries2.sort((a,b) => a.population>b.population?-1:1);
                if(action.payload === "PmM") return allCountries2.sort((a,b) => a.population>b.population?1:-1);
                if(action.payload === "AZ") return state.countries;
                if(action.payload === "ZA") return allCountries2.sort((a,b)=> a.name>b.name?-1:1);
            }      
            return {
                ...state,
                countries: orderPopulation()
            }
        default:
            return state;
        
    }
}

export default rootReducer;
