import React, { useEffect, useState } from "react";
import { Link, useHistory } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { getCountries, postActivity } from "../actions";

export default function ActivityCreate() {
    const dispatch = useDispatch();
   // const activities = useSelector(state => state.activities);
    const countries = useSelector(state => state.countries)

    const [input, setInput] = useState({
        name:"",
        difficulty:"",
        duration: "",
        season:"",
        country:[]
    })

    useEffect(() => {
        //dispatch(getActivities());
        dispatch(getCountries());
    },[]);

    function handleChange(e) {
        setInput({
            ...input,
            [e.target.name]: e.target.value
        })
    }

    function handleCheck(e) {
        if(e.target.checked) {
            setInput({
                ...input,
                season: e.target.value
            })
        }
    }
    function handleSelect(e) {
        if(!input.country.includes(e.target.value)){
            setInput({
                ...input, 
                country:[...input.country,e.target.value]
            })
        }
    }
    function handleSubmit(e) {
        e.preventDefault();
        console.logh(input);
        dispatch(postActivity(input));
        alert("Actividad creada con éxito!");
        setInput({
            name:"",
            difficulty:"",
            duration: "",
            season:"",
            country:[]
        })
    }
    return(
        <div>
            <Link to="/home"><button>Volver</button></Link>
            <h1>Creá una actividad turística!</h1>
            <form>
                <div>
                    <label>Nombre de la actividad: </label>
                    <input type="text" value={input.name} name="name" onChange={e=> handleChange(e)}/>
                </div>
                <div>
                    <label>Dificultad (1 a 5): </label>
                    <input type="number" value={input.difficulty} name="difficulty" onChange={e=> handleChange(e)}/>
                </div>
                <div>
                    <label>Duracion (en horas): </label>
                    <input type="number" value={input.duration} name="duration" onChange={e=>handleChange(e)}/>
                </div>
                <div>
                    <label>Estación: </label>
                    <label><input type="checkbox" value="Verano" name="Verano" onChange={e => handleCheck(e)}/>Verano</label>
                    <label><input type="checkbox" value="Otoño" name="Otoño" onChange={e => handleCheck(e)}/>Otoño</label>
                    <label><input type="checkbox" value="Invierno" name="Invierno" onChange={e => handleCheck(e)}/>Invierno</label>
                    <label><input type="checkbox" value="Primavera" name="Primavera" onChange={e => handleCheck(e)}/>Primavera</label>
                </div>
                <div>
                <label>Pais: </label>
                <select onChange={e => handleSelect(e)}>
                    {countries.map( c => (
                        <option value={c.name}>{c.name}</option>
                    ))}
                </select>
                <ul><li>{input.country.map(i => i +", ")}</li></ul>
                </div>
                <button type="submit">Crear actividad</button>
            </form>
        </div>
    )
}
