import React, { useEffect, useState } from "react";
import { Link, useHistory } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { getActivities, getCountries } from "../actions";

export default function ActivityCreate() {
    const dispatch = useDispatch();
   // const activities = useSelector(state => state.activities);
    const countries = useSelector(state => state.countries)

    const [input, setInput] = useState({
        name:"",
        difficulty:"",
        duration: "",
        season:"",
        country:[]
    })

    useEffect(() => {
        //dispatch(getActivities());
        dispatch(getCountries());
    },[]);

    // const handleChange(e) => {

    // }

    return(
        <div>
            <Link to="/home"><button>Volver</button></Link>
            <h1>Creá una actividad turística!</h1>
            <form>
                <div>
                    <label>Nombre de la actividad: </label>
                    <input type="text" value={input.name} name="name" />
                </div>
                <div>
                    <label>Dificultad (1 a 5): </label>
                    <input type="number" value={input.difficulty} name="difficulty"/>
                </div>
                <div>
                    <label>Duracion (en horas): </label>
                    <input type="number" value={input.duration} name="duration"/>
                </div>
                <div>
                    <label>Estación: </label>
                    <label><input type="checkbox" value={input.season} name="Verano"/>Verano</label>
                    <label><input type="checkbox" value={input.season} name="Otoño"/>Otoño</label>
                    <label><input type="checkbox" value={input.season} name="Invierno"/>Invierno</label>
                    <label><input type="checkbox" value={input.season} name="Primavera"/>Primavera</label>
                </div>
                <div>
                <label>Pais: </label>
                <select>
                    {countries.map( c => (
                        <option value={c.name}>{c.name}</option>
                    ))}
                </select>
                </div>
                <button type="submit">Crear actividad</button>
            </form>
        </div>
    )
}
