import { GET_COUNTRIES, FILTER_BY_CONTINENT, GET_COUNTRY } from "../actions";
const initialState = {
    countries: [],
    allCountries: [],
}

function rootReducer (state=initialState, action) {
    switch(action.type) {
        case GET_COUNTRIES:
            return{
                ...state,
                countries: action.payload.sort((a,b)=> a.name>b.name?1:-1),
                allCountries: action.payload.sort((a,b)=> a.name>b.name?1:-1)
            }
        case FILTER_BY_CONTINENT:
            const allCountries = state.allCountries;
            const continentFilter = action.payload === "all"? allCountries: allCountries.filter( a => a.continent === action.payload)
            return {
                ...state,
                countries: continentFilter
            }
        // case GET_COUNTRY:
        //     const allCountries2 = state.allCountries;
        //     const countrySearch = allCountries2.filter( a=> a.name.toUpperCase() === action.payload.toUpperCase())
        //     return {
        //         ...state,
        //         countries: countrySearch
        //     }
        default:
            return state;
        
    }
}

export default rootReducer;
