import React from "react";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getCountries, filterByContinent, getCountry, orderByPopulation } from "../actions";
import Country from "./Country";
import Paginado from "./Paginado";

export default function Home() {
    const dispatch = useDispatch();
    const allCountries = useSelector(state => state.countries);
    useEffect(() => {
        dispatch(getCountries());
    },[]);
    // const [pais, setPais] = useState("");
    // const handleInput = e => {
    //     setPais(e.target.value)
    // }
    const [orden, setOrden] = useState("");
    const [currentPage, setCurrentPage] = useState(1);
    const [countriesPerPage, setCountriesPerPage] = useState(10);
    const indexOfLastCountry = currentPage * countriesPerPage;
    const indexOfFirstCountry = indexOfLastCountry - countriesPerPage;
    const currentCountries = allCountries.slice(indexOfFirstCountry, indexOfLastCountry);

    const paginado = pageNum => {
        setCurrentPage(pageNum);
    }

    function handleFilterCont(e) {
        dispatch(filterByContinent(e.target.value))
    }

    // const [pais, setPais] = useState("");    
    // function handleSearch(e) {
    //     e.preventDefault();
    //     dispatch(getCountry(e.target.value))
    // }
    function handleOrder() {
        dispatch(orderByPopulation());
        setCurrentPage(1);
        setOrden(`Ordenado por población`);
    }

    return(
        <div>
            <div>
                <input type="text" placeholder="Ingresa país..." />
                <button type="submit">Buscar</button>
            </div>
            <div>
            <a>Ordenar: </a>
            <button>A-Z</button>
            <button onClick={dispatch(orderByPopulation)}>Población</button>
            <select>
                <option value="asc">↓</option>
                <option value="desc">↑</option>
            </select>
            </div>
            <div>
            <a>Filtrar por: </a>
            <select name="continente" onChange={e => handleFilterCont(e)}>
                <option value="all">Todos</option>
                <option value="Africa">Africa</option>
                <option value="North America">America del Norte</option>
                <option value="South America">America del Sur</option>
                <option value="Antarctica">Antarctica</option>
                <option value="Asia">Asia</option>
                <option value="Europe">Europa</option>
                <option value="Oceania">Oceanía</option>
            </select>
            <select name="actividad">
                <option value="act">--</option>
            </select>
            </div>
            <Paginado 
            countriesPerPage={countriesPerPage}
            allCountries={allCountries.length}
            paginado={paginado}
            />
            <div>
                {currentCountries?.map( p =>
                    <Country
                    key = {p.id}
                    flag = {p.flag}
                    name = {p.name}
                    continent = {p.continent}
                    />
                    )}
            </div>
        </div>
    )
}
